package Skipass;import java.util.Date;/** * Created by Best on 02.12.2015. */public class SkipassRegister {    public static SkipassWeekend newSkipassWeekend(int slopesOrDays, Date startDate, Date endDate, SkipassType skipassType) {        SkipassWeekend skipass = new SkipassWeekend( slopesOrDays, startDate, endDate, skipassType);        return skipass;    }    public static SkipassWorkingDay newSkipassWorkingDay(int slopesOrDays, Date startDate, Date endDate, SkipassType skipassType) {        SkipassWorkingDay skipass = new SkipassWorkingDay( slopesOrDays, startDate, endDate, skipassType);        return skipass;    }    public static SkipassSeason newSkipassSeason() {        SkipassSeason skipass = new SkipassSeason(1, SkipassConstants.MORNING, SkipassConstants.NIGHT, SkipassType.TIME_SKIPASS);        return skipass;    }}